export type OrderCreatedV1 = {
  type: 'order.created';
  version: 1;
  id: string;
  userId: string;
  totalCents: number;
  createdAt: string;
};

export function isOrderCreatedV1(x: any): x is OrderCreatedV1 {
  return (
    x &&
    x.type === 'order.created' &&
    x.version === 1 &&
    typeof x.id === 'string' &&
    typeof x.userId === 'string' &&
    typeof x.totalCents === 'number' &&
    typeof x.createdAt === 'string'
  );
}

export function parseSnippetEmbed(line: string) {
  const match = line.match(/^@snippet\(([^,\)]+)(?:,\s*([^\)]+))?\)$/);
  if (!match) return null;
  const id = match[1].trim();
  const caption = (match[2] || '').trim();
  return { id, caption };
}
